import React from "react";
import styled from "styled-components";
import boxImg from "../images/buyBox.svg"


const Container = styled.div`
    box-shadow: 0px 2px 8px 0px #00000022;
    background-color:pink;
    border-radius:10%;
    display:inline-block;
    height:47%;
    width:23%;
    margin:5px;
`;

const Ad = styled.div`
    display:flex;
    height:85%;
    flex-direction:column;
`;

const BuySection = styled.div`
    display:flex;
    height:15%;
    justify-content:center;
    align-items: baseline;
    font-family:Montserrat, Arial;
    font-size: 14px;
    font-weight: 600;
    line-height: 18px;
    text-align: left;
    color:white;
    background-color:#0F52BA;
    border-radius:0 0 2vh 2vh;
`;

const ItemDesc = styled.div`
    font-family: Montserrat;
    font-size: 16px;
    font-weight: 400;
    line-height: 19px;
    text-align: left;
    color:#2C2C2C;


`;

const ItemButtom = (props:any) => {

    return (
        <Container>
           {/* <div style={{display:"flex"}}> */}
                <Ad>
                    <img src={boxImg} alt="imagem-do-produto" />
                    <div style={{ backgroundColor:'white'}}>
                        <ItemDesc>Texto texto texto</ItemDesc>

                    </div>
                </Ad>
                <BuySection>
                    <img src={boxImg} alt="buy-box-image" style={{margin:"5px"}}/>
                    COMPRAR
                </BuySection>
           {/* </div> */}
        </Container>
    )

}

export default ItemButtom;
