import React, {useEffect, useState} from "react";
import styled from "styled-components";
import { motion, useAnimate } from "framer-motion";


const B = styled.button`
`;

const View = styled.div`

    box-shadow: -5px 0px 6px 0px #00000021;
    position:fixed;
    height:100%;
    width:30%;
    background-color:#0F52BA;
    top:0px;
    z-index:-1; 
    border-width:5px;
    //border-color:red; 
    //border-style:solid;
    box-sizing:border-box;

`;

const Cart = (props:any) => {

    const [scope, animate] = useAnimate()

    useEffect(()=>{
     
        if(props.show){
            animate(scope.current, {x:"70vw", zIndex:1})
        }else
            animate(scope.current, {x:"105vw"})

           
    }, [props.show])

   
    // useEffect(()=>{

    //     if(showCart){
    //         animate(scope.current, {x:"70vw"})
    //     }else
    //         animate(scope.current, {x:"100vw"})
    // }, [showCart])

    function onClose(){
       // props.onClose();
    }

    return(
        <View ref={scope}>

           
            
            <B onClick={props.onClose}></B>
            

        </View>

        // <motion.div
        // ini>

        // </motion.div>
    )

}




export default Cart;
