import React from 'react';
import './App.css';
import styled from 'styled-components';
import Header from './components/head';
import Footer from './components/foot';
import ItemButtom from './components/itemButton';
import ProductsProvider, {ProductsContext} from './contexts/products';
import { QueryClient, QueryClientProvider, useQuery } from "@tanstack/react-query";


const Container = styled.div`

  display:flex;
  color:black; 
  background-color:#F9F9F9; 
  height:100%;
  width:100%;
  justify-content: center;
  align-items: center;
`;

const Wrapper = styled.div`

  height:75%;
  max-height:75vh;
  width:65% ;
  overflow-y: scroll;
  display:block;
  padding-left:50px;
  //scrollbar-width: thin;
  &::-webkit-scrollbar {

    width: 1px;
    //background: #b3b3b3;

  }
  &::-webkit-scrollbar-thumb {
    background: black;
    width: 0px;
    -webkit-border-radius: 1ex;
    -webkit-box-shadow: 0px 1px 1px rgba(0, 0, 0);
}


`;


function App() {

  const queryClient = new QueryClient()


  async function GetProducts (){
    const response = await fetch("https://mks-frontend-challenge-04811e8151e6.herokuapp.com/api/v1/products?page=1&rows=8&sortBy=id&orderBy=ASC")
    if (!response.ok)
        throw new Error("Os dados não foam carregados")

    response.json().then(res=>{console.log(res)})

    return response.json()
  }

  const {data, isLoading, isError } = useQuery({queryKey:["dados"], queryFn:GetProducts})


  return (
    <QueryClientProvider client={queryClient}>
      <ProductsProvider>
        {console.log(data)}
          {/* // <div className="App"  style={{display:'flex', backgroundColor:'black', flexDirection:'column', marginBottom:0}}> */}
          <div style={{display:'flex', flex:1, flexDirection:'column', height:'100%'}}> 
            <Header/>
              <Container>
                <Wrapper>
                  <ItemButtom/>
                  <ItemButtom/>
                  <ItemButtom/>
                  <ItemButtom/>
                  <ItemButtom/>
                  <ItemButtom/>
                  <ItemButtom/>
                  <ItemButtom/>
                  <ItemButtom/>
                  <ItemButtom/>

                </Wrapper>
              </Container>
            <Footer/>
          </div>



        {/* // </div> */}
      </ProductsProvider>
    </QueryClientProvider>
  );
}

export default App;
