import { ProductsContext } from './contexts/products';

const Container = styled.div`

  display:flex;
  color:black; 
  background-color:#F9F9F9; 
  height:100%;
  width:100%;
  justify-content: center;
  align-items: center;
`;

const Wrapper = styled.div`

  height:75%;
  max-height:75vh;
  width:65% ;
  overflow-y: scroll;
  display:block;
  padding-left:50px;
  //scrollbar-width: thin;
