    background: black;
    width: 0px;
    -webkit-border-radius: 1ex;
    -webkit-box-shadow: 0px 1px 1px rgba(0, 0, 0);
}


`;

function App() {


  const {data, isLoading, isError} = useContext(ProductsContext);

