import React, {useEffect, useState} from "react";
import styled from "styled-components";
import { useAnimate } from "framer-motion";

const View = styled.div`
    display:flex;
    flex-direction:column;
    box-shadow: -5px 0px 6px 0px #00000021;
    position:fixed;
    height:100%;
    width:30%;
    background-color:#0F52BA;
    top:0px;
    z-index:-1; 
    border-width:5px;
    //border-color:red; 
    //border-style:solid;
    box-sizing:border-box;
    transform-origin: top right;
`;
const Header = styled.div`
    display:flex;
    justify-content: space-between;
    height:90px;
    //background-color:red;
    box-sizing:border-box;
    padding:25px 30px;  
    bottom:0px;
`;
const Title = styled.div`
    font-family: Montserrat;
    font-size: 100%;
    font-weight: 700;
    line-height: 32.91px;
    text-align: left;
    color:white;
    width:37%;
    //background-color:green;
`;
const CloseButton = styled.button`
    border-radius:100%;
    height:38px;
    width:38px;
    align-self:center;
    box-sizing: border-box; 
    border-width:0px;
    font-size:100%;
    font-family: Montserrat;

    &:hover {
        color:black;
        background-color:white;
    }
    &:focus{
        color:white;
        background-color:black;
    }
    &:active{
        color:white;
        background-color:black;
    }
    &:not(:active){
        color:white;
        background-color:black;
    }
    &:not(:active):hover{
        color:black;
        background-color:white;
    }
`;
const ContainerItems = styled.div`

`;
const Label = styled.div`
    font-family: Montserrat;
    font-size: 28px;
    font-weight: 700;
    text-align: center;
    width:25%;
    color:white;
    overflow:hidden;
    text-overflow:"...";
`;
const PriceLabel = styled.div`
    font-family: Montserrat;
    font-size: 98%;
    font-weight: 700;
    text-align: right;
    width:75%;
    color:white;
    marginRight:10px;
    overflow:hidden;
    text-overflow:"...";
`;


const Cart = ({children, ...props}:any) => {

    const [scope, animate] = useAnimate()

    useEffect(()=>{
     
        if(props.show){
            animate(scope.current, {x:"170%", zIndex:1})
        }else
            animate(scope.current, {x:"420%"})

           
    }, [props.show])

   
    return(
        <View ref={scope}>
 
            <Header>
                <Title>Carrinho de compras</Title>
                <CloseButton onClick={props.onClose}>X</CloseButton>
            </Header>
            <div style={{backgroundColor:'red', height:'100%'}}></div>
            <div style={{backgroundColor:'blue', height:'97px', boxSizing:'border-box',  display:"flex", alignItems:'center', paddingRight:"10%", paddingLeft:"5%"}}>
                <Label style={{ boxSizing:'border-box'}}>
                    Total:
                </Label>
                <PriceLabel >
                    R${props.price}
                </PriceLabel>
            </div>
            <div style={{backgroundColor:'black', height:'97px', color:'white'}}>{children}</div>

        </View>

        // <motion.div
        // ini>

        // </motion.div>
    )

}




export default Cart;
