import React, {useEffect, useState} from "react";
import styled from "styled-components";
import { motion, useAnimate } from "framer-motion";


const Cart = (props:any) => {

    const [scope, animate] = useAnimate()
    const [showCart, useShowCart] = useState(false)

    useEffect(()=>{

        if(props.show){
            animate(scope.current, {x:"70vw"})
        }else
            animate(scope.current, {x:"100vw"})
    }, [props.show, showCart])

    

    return(
        <div ref={scope} style={{position:'absolute', height:'100%', width:'30%', backgroundColor:'#0F52BA', 
        top:"0px", zIndex:1, borderWidth:"5px", borderColor:'red', borderStyle:'solid', 
        boxSizing:'border-box'}}>
            <button onClick={props.onClose}></button>

        </div>

        // <motion.div
        // ini>

        // </motion.div>
    )

}




export default Cart;
