import React from "react";
import styled from "styled-components";
import boxImg from "../images/buyBox.svg"


const Container = styled.button`
    box-shadow: 0px 2px 8px 0px #00000022;
    background-color:white;
    border-radius:10%;
    display:inline-block;
    height:47%;
    width:23%;
    margin:5px;
    box-sizing: border-box; 
    outline:0px; 
    border-width:0px;

    &:hover {
        background-color:gray;

    }
`;

const Ad = styled.div`
    display:flex;
    height:85%;
    flex-direction:column;
`;


const ItemTitle = styled.div`
    height:100%;
    font-family: Montserrat;
    font-size: 16px;
    font-weight: 400;
    line-height: 19px;
    text-align: left;
    color:#2C2C2C;
    width:60%;
    padding: 5px 5px 5px px;
    text-overflow: "...";
    overflow:hidden;
    box-sizing: border-box; 
    display:inline-block 
    //background-color:#0F52BA;
`;

const ItemDesc = styled.div`
    height:20%;
    max-height:20vh;
    font-family: Montserrat;
    font-size: 10px;
    font-weight: 300;
    line-height: 12px;
    text-align: left;
    color:#2C2C2C;
    padding: 10px;
    text-overflow: "...";
    overflow:hidden;
    box-sizing: border-box; 
    display:inline-block 

`;

const BuySection = styled.div`
    display:flex;
    height:15%;
    width:103%;
    position:relative;
    left:-3px;
    justify-content:center;
    align-items: baseline;
    font-family:Montserrat, Arial;
    font-size: 14px;
    font-weight: 600;
    line-height: 18px;
    text-align: left;
    color:white;
    background-color:#0F52BA;
    border-radius:0 0 2.5vh 2.5vh;
    overflow:hidden
`;

const ItemButtom = (props:any) => {

    return (
        <Container>
           {/* <div style={{display:"flex"}}> */}
                <Ad>
                    <img src={props.image||boxImg} alt="imagem-do-produto" height={"50%"}/>
                    <div style={{ display:'flex', height:"25%", justifyContent:'space-around'}}>
                        <ItemTitle>{props.title||"Texto Texto Texto Texto"}</ItemTitle>
                        <button></button>
                    </div>
                    <ItemDesc>
                        {props.description||
                        "descricao do item descrição do item deiscrinao do item descriççao do descriescricao do item descrição do item deiscrinao do item descriççao do escricao do item descrição do item deiscrinao do item descriççao do descri do item descrição do i"}
                    </ItemDesc>
                </Ad>
                <BuySection>
                    <img src={boxImg} alt="buy-box-image" style={{margin:"5px"}}/>
                    COMPRAR
                </BuySection>
           {/* </div> */}
        </Container>
    )

}

export default ItemButtom;
