import React, {useState, useContext} from "react";
import styled from "styled-components";


const Container = styled.div`
    width:78.7%;
    height:97px;
    background-color:white;
    border-radius:8px;
`;
const RemoveButton = styled.button`
    position:relative;
    left:97%;
    top:-10%;
    height:18px;
    width:18px;
    background-color:black;
    border-width:0px;
    border-radius:50%;
    font-size:65%;
    font-family: Montserrat;
    text-align:center;
    
    &:hover {
        color:black;
        background-color:white;
    }
    &:focus{
        color:white;
        background-color:black;
    }
    &:active{
        color:white;
        background-color:black;
    }
    &:not(:active){
        color:white;
        background-color:black;
    }
    &:not(:active):hover{
        color:black;
        background-color:white;
    }
`;
const Title = styled.div`
    font-family: Montserrat;
    font-size: 100%;
    font-weight: 700;
    line-height: 32.91px;
    text-align: left;
    color:white;
    width:37%;
    //background-color:green;
`;
const PriceLabel = styled.div`
    font-family: Montserrat;
    font-size: 98%;
    font-weight: 700;
    text-align: right;
    width:75%;
    color:white;
    marginRight:10px;
    overflow:hidden;
    text-overflow:"...";
`;

const CartItem = ({children, ...props}:any) =>{

    return(
        <Container>
            <RemoveButton>X</RemoveButton>
            <div style={{backgroundColor:"black"}}></div>
        </Container>
    )
}

export default CartItem;
