import React, {useEffect, useState} from "react";
import styled from "styled-components";
import { motion, useAnimate } from "framer-motion";


const Cart = (props:any) => {

    const [scope, animate] = useAnimate()
    const [showCart, useShowCart] = useState()

    useEffect(()=>{
        animate(scope.current, {x:"100%"})
    }, [props.show])

    return(
        <div ref={scope} style={{position:'absolute', height:'100%', width:'30%', backgroundColor:'#0F52BA', 
        top:"0px", left:"70%", zIndex:1, borderWidth:"5px", borderColor:'red', borderStyle:'solid', 
        boxSizing:'border-box'}}>
            <button onClick={()=>useShowCart}></button>

        </div>

        // <motion.div
        // ini>

        // </motion.div>
    )

}

export default Cart;
